mapper.select:
SELECT ${params.xColumn}, ROUND(AVG(${params.yColumn}),2) average,COUNT(1) count FROM medical_plan
1=1 ${ew.sqlSegment}
GROUP BY ${params.xColumn}
LIMIT 20

select.if:
SELECT DATE_FORMAT(${params.xColumn},'%Y-%m-%d') ${params.xColumn}, ROUND(AVG(${params.yColumn}),2) average,COUNT(1) count FROM medical_plan
1=1 ${ew.sqlSegment}
GROUP BY DATE_FORMAT(${params.xColumn},'%Y-%m-%d')

SELECT DATE_FORMAT(${params.xColumn},'%Y-%m') ${params.xColumn}, ROUND(AVG(${params.yColumn}),2) average,COUNT(1) count FROM medical_plan
1=1 ${ew.sqlSegment}
GROUP BY DATE_FORMAT(${params.xColumn},'%Y-%m')

SELECT DATE_FORMAT(${params.xColumn},'%Y') ${params.xColumn}, ROUND(AVG(${params.yColumn}),2) average,COUNT(1) count FROM medical_plan
1=1 ${ew.sqlSegment}
GROUP BY DATE_FORMAT(${params.xColumn},'%Y')

mapper.select:
SELECT ${params.column}, COUNT(1) count FROM medical_plan
1=1 ${ew.sqlSegment}
GROUP BY ${params.column}
LIMIT 20
